// Bootstrap JavaScript - Minimal version for demo
(function() {
    'use strict';
    
    console.log('Bootstrap JS loaded for Bundler App');
    
    // Simple navbar toggle functionality
    document.addEventListener('DOMContentLoaded', function() {
        const togglers = document.querySelectorAll('.navbar-toggler');
        togglers.forEach(function(toggler) {
            toggler.addEventListener('click', function() {
                const target = document.querySelector(this.getAttribute('data-target'));
                if (target) {
                    target.classList.toggle('show');
                }
            });
        });
    });
    
    // Simple alert functionality
    window.showAlert = function(message) {
        alert(message);
    };
    
})();
